import axios from 'axios';

// ตั้งค่า Base URL สำหรับ Backend
const API = axios.create({
    baseURL: 'http://127.0.0.1:8000/api/', // URL ของ Backend
});

// ฟังก์ชันสำหรับดึงข้อมูล Tickets
export const getTickets = (filters = {}) => {
    const params = new URLSearchParams(filters).toString();
    return API.get(`tickets/?${params}`); // ส่ง Query Parameters เช่น ?status=Pending
};

// ฟังก์ชันสำหรับสร้าง Ticket ใหม่
export const createTicket = (ticket) => {
    return API.post('tickets/', ticket);
};

// ฟังก์ชันสำหรับอัปเดตสถานะหรือข้อมูล Ticket
export const updateTicket = (id, data) => {
    return API.patch(`tickets/${id}/`, data);
};
